import useMediaQuery from "../hooks/useMediaQuery";
import {motion} from "framer-motion";
import AnchorLink from "react-anchor-link-smooth-scroll";

const Landing=({setSelectedPage})=>{
    const isDesktop=useMediaQuery("(min-width: 1060px)");
    return (
        <section id="home" className="md:flex md:justify-between md:items-center md:h-full gap-16 py-10" >
            {/*image section*/}
            <div className="md:order-2 flex justify-center basis-3/5 z-10 mt-16 md:mt-32">
                {isDesktop?(
                    <div
                        className="relative z-0 ml-20 before:absolute before:-top-20 before:-left-1
                        before:rounded-t-[400px] before:w-full before:max-w-[400px] before:h-full
                        before:border-2 before:border-blue before:z-[-1]"
                    >
                        <img
                        alt="profile"
                        className="hover:filter hover:saturate-150 transition duration-500 z-10 w-full
                        max-width-[400px] md:max-w-[600px]"
                        src="assets/pf.png" 
                        height="400px"
                        />
                    </div>

                ):(
                    <img
                        alt="profile"
                        className="hover:filter hover:saturate-150 transition duration-500 z-10 w-full
                        max-width-[400px] md:max-w-[600px]"
                        src="nassets/pf.png" 
                    />
                )}

                {/*main section*/}
                <div className="z-30 basis-2/5 mt-12 md:mt-32">
                    {/* headings */}
                    <motion.div
                        initial="hidden"
                        whileInView="visible"
                        viewport={{once:true,amount:0.5}}
                        transition={{duration:0.5}}
                        variants={{
                            hidden:{opacity:0, x:-50},
                            visible:{opacity:1,x:0}
                        }}
                    >
                        <p className="text-6xl font-playfair z-10 text-centeer md:text-start">
                            Divyansh{""}
                            <span 
                                className="xs:relative xs:text-deep-blue xs:font-semibold z-20
                                xs:before:content-brush before:absolute before:-left-[-25px]
                                before:-top-[-70px] before:z-[-1]"
                            >Esper
                            </span>
                            <p className="mt-10 mb-7 text-sm text-center md:text-start">
                                Hello how are you, random placeholder text
                                working working

                            </p>
                        </p>
                    </motion.div>
                </div>
            </div>
        </section>
    )
}

export default Landing;
